let client = new (require('.').Client)();
client.connect({ clientId: '1016727168765657148', scopes: ['identify'] }).then(console.log).catch(console.error);

client.on('ready', _ => {
    client.setActivity({
        details: 'Discord Rich Presence',
        state: 'Revamp Test',
        startTimestamp: Date.now(),
        largeImageKey: 'https://cdn.icon-icons.com/icons2/2568/PNG/512/code_coding_icon_153713.png',
        buttons: [
            {
                label: 'Sex',
                url: 'https://www.youtube.com/watch?v=dQw4w9WgXcQ'
            }
        ]
    }).then(_ => console.log('set activity')).catch(console.error);

    client.subscribe('ACTIVITY_JOIN');
    client.subscribe('ACTIVITY_JOIN_REQUEST');

    client.on('ACTIVITY_JOIN', data => {
        console.log('ACTIVITY_JOIN', data);
    });
});

client.on('close', _ => {
    console.log('close');
});